package JavaPractice;

public class Addation {
	
	
	public static  int  calculation(int x, int y)
	
	{
		int z=x+y;
		return z;
	}
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=10;
		int sum=calculation(a, b);
		System.out.println("Sum: "+ sum);

	}

}
