package JavaPractice;

public class evennumbersum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int no=5;
		
		int sum=0;
		
		for(int i=0; i<no; i++)
		{
			System.out.println(2*i);
			sum=sum+(2*i);
			
			}
		System.out.println("Total of sum is : " + sum);
		
			
		
	}

}
