
public class newclassandobject {

	
	public static class k1{
		
		String first;
		k1()
		{
			String first="Java program";
		}
		
		
		void disp()
		{
			System.out.println(first);
		}
		
		
	}
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		k1 reff=new k1();
		reff.disp();
		
		
		
	}

}
